package io.firebus.adapters.http;

import java.io.IOException;
import java.io.PrintWriter;
import java.util.List;

import javax.servlet.ServletException;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import io.firebus.Firebus;
import io.firebus.data.DataMap;

public class LogoutHandler extends HttpHandler {
	protected List<SecurityHandler> securityHandlers;
		
	public LogoutHandler(HttpGateway gw, Firebus f, DataMap c) {
		super(gw, f, c);
	}
	
	public void setSecuritytHandlers(List<SecurityHandler> sh) {
		securityHandlers = sh;
	}

	protected void httpService(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
		if(securityHandlers != null) {
			for(SecurityHandler sh: securityHandlers) {
				sh.enrichLogoutResponse(resp);
			}
		}
        PrintWriter writer = resp.getWriter();
        writer.println("<html><head><title>Login</title><meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"><style>");
        writer.println("body{}");
        writer.println(".main{position:fixed; display:flex; flex-direction:column; align-items:center; top:50%; left: 50%; transform: translate(-50%, -50%); font-family:sans-serif; font-size:larger; border:1px solid lightgrey; padding:15px; border-radius:5px;}");
        writer.println(".logo{padding:5px;width:40px;}");
        writer.println(".title{color:black; padding:10px; white-space:nowrap;}");
        writer.println(".return{padding: 5px;}");
        writer.println(".option{display:flex; flex-direction:row; align-items:center;padding:5px;}");
        writer.println("a{display:flex; flex-direction:row; align-items:center;}");
        writer.println("a:link {color:grey; text-decoration:none;} a:visited  {color:grey; text-decoration:none;} a:hover {color:black; text-decoration:none;} a:active {color:grey; text-decoration:none;}");
        writer.println("img {padding-right:10px;}");
        writer.println("</style></head>");
        writer.println("<body><div class=\"main\">");
        writer.println("<img class=\"logo\" src=\"data:image/png;base64,\">");
        writer.println("<div class=\"title\">You have logged out</div>");
        writer.println("<div class=\"return\"><a id=\"return\" href=\"/\">Return</a></div>");
        writer.println("<script>r = (new URL(document.location)).searchParams.get('return'); a = (new URL(document.location)).searchParams.get('auto'); if(a == 'return' && r != null) {setTimeout(function() {window.location = r}, 2000);} else if(a == 'close') {setTimeout(function() {window.close()}, 2000);} else {document.getElementById('return').href = (r != null ? r : '/');}</script>");
        writer.println("</div></body></html>");
		
	}


}
